} else if (autoShrinkIn == 1) {
    if (autoShrinkOut == 1 && end > segLength && segLength <= 50) {
        if (trail == 1) {
            $bm_rt = linear(end, $bm_sub(100, segLength), 100, linear(taperOut, 0, 100, strokeWidth, strokeTaper), trailWidth);
        } else {
            $bm_rt = linear(end, $bm_sub(100, segLength), 100, linear(taperOut, 0, 100, strokeWidth, strokeTaper), 0);
        }
    } else {
        $bm_rt = linear(end, 0, segLength, 0, linear(taperOut, 0, 100, strokeWidth, strokeTaper));
    }
} else if (autoShrinkOut == 1) {
    if (trail == 1) {
        $bm_rt = linear(end, $bm_sub(100, segLength), 100, linear(taperOut, 0, 100, strokeWidth, strokeTaper), trailWidth);
    } else {
        $bm_rt = linear(end, $bm_sub(100, segLength), 100, linear(taperOut, 0, 100, strokeWidth, strokeTaper), 0);
    }
} else {
    $bm_rt = linear(taperOut, 0, 100, strokeWidth, strokeTaper);
}
