import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Autoplay } from 'swiper/modules';

import 'swiper/css';
import 'swiper/css/navigation';
import styles from "./MainBanner.module.scss";

import bg1 from '../assets/images/bg1.png';

function MainBanner() {
  return (
    <article>
      <Swiper 
        navigation={true} 
        loop={true}
        modules={[Navigation, Autoplay]} 
        autoplay={{
          delay: 2500,
          disableOnInteraction: false,
        }} 
        className={styles.mainSwiper}>
        <SwiperSlide className={styles.bg}> <img src="https://placeholder.co/800x500/f46/000?text=img1" alt="img1" /></SwiperSlide>
        <SwiperSlide className={styles.bg}><img src={bg1} alt="bg1" /></SwiperSlide>
        <SwiperSlide className={styles.bg}><img src={process.env.PUBLIC_URL + "./images/bg3.png"} alt="img3" /></SwiperSlide>
        <SwiperSlide className={`${styles.bg}.${bg4}`}>Slide 4</SwiperSlide>
      </Swiper>
    </article>
  );
}

export default MainBanner;
